import React from 'react';
import {
    Grid,
    Card,
    CardContent,
    Button,
    CardHeader,
    TextField,
    MenuItem
  } from "@material-ui/core";
  import { makeStyles } from '@material-ui/core/styles';
  import CreateIcon from '@material-ui/icons/Create';



  const hostels = [
    {
      value: 'H1',
      label: 'H1',
    },
    {
      value: 'H2',
      label: 'H2',
    },
    {
      value: 'H3',
      label: 'H3',
    },
    {
      value: 'H4',
      label: 'H4',
    },
  ];


  const useStyles = makeStyles((theme) => ({
    form: {
        width: '100%', // Fix IE 11 issue.
        marginTop: theme.spacing(1),
      }, 
    }))
  
  
  const AddNotice= () => {
    const classes = useStyles();
    return (
        <>
      <Grid container>

          <Grid item md="6">
            <Card>
              <CardContent>
                
                <form className={classes.form}>
                 
                    <TextField
                     fullWidth
                      label="Notice Title"
                      icon="envelope"
                      group
                      type="text"
                      validate
                      error="wrong"
                      success="right"
                    />
                    
                    <TextField
                      fullWidth
                      label="Type your description"
                      icon="lock"
                      group
                      type="text"
                      validate
                    />
                     <TextField 
                    
                     icon="lock"
                     group
                     type="Date"
                     validate/>

                   <TextField
          id="standard-select-currency"
          select
          label="Select"
          helperText="Please select the hostel"
          variant="standard"
        >
            {hostels.map((option) => (
            <MenuItem key={option.value} value={option.value}>
              {option.label}
            </MenuItem>
          ))}
        </TextField>
                     

                  
  
                <div className="text-center mt-4">
                  <Button
                    color="Primary"
                    className="mb-3"
                    type="submit"
                  >
                      Add Notice
                    
                  </Button>
                </div>
                </form>
              
              </CardContent>
            </Card>
          </Grid>
      </Grid>
      </>
    );
  };
  
  export default AddNotice;
